anki.fieldTemplates:
{{#*inline "test"}}
    {{~#scope~}}
        {{~#set "first-dictionary" null}}{{/set~}}
        {{~#if modeKanji~}}
            ...
        {{~else if (op "||" group merge)~}}
            {{~#each definition.definitions~}}
                {{~#if (op "===" null (get "first-dictionary"))~}}
{{~#set "first-dictionary" dictionary~}}{{~/set~}}
                {{~/if~}}
                {{~#if (op "===" dictionary (get "first-dictionary"))~}}
{{> glossary-single brief=../brief compactGlossaries=../compactGlossaries data=../.}}
                {{~/if~}}
            {{~/each~}}
        {{~else~}}
            {{~> glossary-single definition brief=brief compactGlossaries=compactGlossaries data=.~}}
        {{~/if~}}
    {{~/scope~}}
{{/inline}}

{{#*inline "glossary-single"}}
    {{~#unless brief~}}
        {{~#if definitionTags~}}{{#each definitionTags}}{{#unless @last}}{{/unless}}{{/each}} {{/if~}}
        {{~#if only~}}({{#each only}}{{{.}}}{{#unless @last}}, {{/unless}}{{/each}} only) {{/if~}}
    {{~/unless~}}
    {{~#if glossary.[1]~}}
        {{~#if compactGlossaries~}}
            {{#each glossary}}{{#multiLine}}{{#regexReplace "^[^\n]*\n" ""}}{{.}}{{/regexReplace}}{{/multiLine}}{{#unless @last}} | {{/unless}}{{/each}}
        {{~else~}}
            <ul>{{#each glossary}}<li>{{formatGlossary ../dictionary .}}</li>{{/each}}</ul>
        {{~/if~}}
    {{~else~}}
        {{~#multiLine}}{{#regexReplace "^[^\n]*\n" ""}}{{glossary.[0]}}{{/regexReplace}}{{/multiLine~}}
    {{~/if~}}
{{/inline}}

